CustomField.formula:
if(TEXT(conference360__Status__c)=='Completed','Event Ended',if(TEXT(conference360__Hide_Register_Button__c)=='Yes','Disabled Event',if( conference360__Event_End_Date_Time_GMT__c  < NOW(),'Event Ended' ,
    if(conference360__Remaining_Capacity__c<=0 ,
        if(conference360__Waitlist_Enabled__c == false ,
            if(conference360__Waitlist_Enabled_On_EventItem__c==true,
                if(ISNULL(conference360__Waitlist_Capacity__c),'Sold Out, Waitlist Open',if(conference360__Remaining_Waitlist_Capacity__c<=0, 'Sold Out, Waitlist Closed','Sold Out, Waitlist Open')),
                if(OR(conference360__Remaining_Waitlist_Capacity__c<=0,ISNULL(conference360__Remaining_Waitlist_Capacity__c)), 'Sold Out','Sold Out, Waitlist Open')
            ),
            if(OR(conference360__Remaining_Waitlist_Capacity__c >0 , ISNULL(conference360__Remaining_Waitlist_Capacity__c)),'Sold Out, Waitlist Open','Sold Out, Waitlist Closed')
        ),
        'Event Active'
    )
)))
